local function DoIOLoad()
    local lg = Logger("Sandex")
    lg:debug("Attempting IO load...")

    -- Credit to @corsix - https://gist.github.com/corsix/6575486
    -- and to @numinit - https://apocrypha.numin.it/talks/lua_bytecode_exploitation.pdf

    -- SHA256: 3FABB12341DC8492CFF2BB90D86550F3D4E90A9B6F8EC9074639A8A61BC4609F - length: 6468
    -- Verification: http://matekos17.f.fazekas.hu/secret/verification/v1_bytecode_hashes.txt
    -- Raw source: http://matekos17.f.fazekas.hu/secret/verification/v1_bytecode_source.luac
    local ex = ""

    if loadstring ~= nil then
        local func = loadstring(ex:gsub("..", function(c)
            return string.char(tonumber(c, 16))
        end))

        if pcall(func) then
            lg:log("Loaded IO library")
            return false
        else
            lg:err("Failed to load IO library - parsing error")
        end
    elseif load ~= nil then
        local func = loadstring(ex:gsub("..", function(c)
            return string.char(c)
        end))

        if pcall(func) then
            lg:log("Loaded IO library")
            return false
        else
            lg:err("Failed to load IO library - parsing error")
        end
    else
        lg:err("Failed to load IO library - no available parsers")
    end

    return true
end
